import gifshot from '../lib/gifshot.js'
import React from 'react'
import { Box, Grid, Button } from 'grommet'
import gifFrames from 'gif-frames'
import { connect } from 'react-redux'
import { object } from 'prop-types'

const CreatePreviewGIF = (timeline) => {
    var frames = []
    for(let i = 0; i < timeline.length; i++) {
        let url = URL.createObjectURL(timeline[i].shot)
        gifFrames({url, frames: 'all'}, (frameData) => {

            var newFrames = frameData.map((item) => item.getImage())
            console.log(newFrames)
            frames.contact(newFrames)
        })
    }
    console.log(frames)
    var urls = frames.map((image) => URL.createObjectURL(image))
    console.log(urls)

    let result
    gifshot.createGIF({
        "images" : urls
    }, function(obj){
        if(!obj.error) {
            console.log("No error")
            result = obj.image
        }
    })

    return result
}

export const PreviewWindowComponent = ({timeline}) => {


    var gifURL = timeline.length > 0 ? URL.createObjectURL(timeline[0].shot) : ""
    if(timeline.length > 0) {
        let gif = CreatePreviewGIF(timeline)
        gifURL = URL.createObjectURL(gif)
    }
    return (
        <Box
            fill
            align="center"
            justify="center"
            gap="small"
            background="dark-1"
        >
            <Box
                width="medium"
                height="medium"
                background="dark-3"
                style={{backgroundImage:`url(${gifURL})`, backgroundSize:'cover', backgroundRepeat: 'no-repeat', backgroundPosition: 'center'}}
            >

            </Box>
            <Box
                round
            >
                <Grid
                    fill
                    areas={
                        [
                            { name: "back", start: [0, 0], end: [0,0] },
                            { name: "play", start: [1, 0], end: [1,0] },
                            { name: "toEnd", start: [2, 0], end: [2,0] },
                        ]
                    }
                    columns={["flex", "flex", "flex"]}
                    rows={["flex"]}
                    gap="small"
                >
                    <Button gridArea="back" label="|<"/>
                    <Button gridArea="play" label=">" primary/>
                    <Button gridArea="toEnd" label=">|"/>

                </Grid>
            </Box>
        </Box>
    )
}

const mapStateToProps = state => ({
        timeline: state.timeline
    })

const mapDispatchToProps = dispathc => ({

    })

export const PreviewWindow = connect(
    mapStateToProps,
    mapDispatchToProps
)(PreviewWindowComponent)
